import devEnv from '../_env_/development.json'
import prodEnv from '../_env_/production.json'

export interface Env {
    ENV: string
    APP_NAME: string
    STORE_KEY: string
    LANGUAGE: {
        DEFAULT_LOCALE: string
        SUPPORTED_LOCALES: string[]
    }
}

let config: Env = {
    ...devEnv,
}

if (process.env.NODE_ENV === 'production') {
    config = {
        ...config,
        ...prodEnv,
    }
}

export default config

import 'intl'
import { addLocaleData } from 'react-intl'

import enLocaleData from 'react-intl/locale-data/en'
import frLocaleData from 'react-intl/locale-data/fr'

import enTranslationMessages from '../_translations_/en.json'
import frTranslationMessages from '../_translations_/fr.json'

import config from 'config'

/**
 * add helpers to format date strings and all the i18n magic
 */
addLocaleData(enLocaleData)
addLocaleData(frLocaleData)

export const formatTranslationMessages: any = (
    locale: string,
    messages: any
) => {
    const defaultFormattedMessages: any =
        locale !== config.LANGUAGE.DEFAULT_LOCALE
            ? formatTranslationMessages(
                  config.LANGUAGE.DEFAULT_LOCALE,
                  enTranslationMessages
              )
            : {}
    const formattedMessages: any = {}
    const messageKeys = Object.keys(messages)

    for (const messageKey of messageKeys) {
        formattedMessages[messageKey] =
            locale === config.LANGUAGE.DEFAULT_LOCALE
                ? messages[messageKey]
                : (formattedMessages[messageKey] =
                      messages[messageKey] ||
                      defaultFormattedMessages[messageKey])
    }

    return formattedMessages
}

export const translationMessages: any = {
    en: formatTranslationMessages('en', enTranslationMessages),
    fr: formatTranslationMessages('fr', frTranslationMessages),
}

import Expo from 'expo'
