import { ActionCreator } from 'redux'
import { ErrorReduxAction, ReduxAction } from '../types'

export const GET_LOCALE_PENDING = 'GET_LOCALE_PENDING'
export const GET_LOCALE_SUCCESS = 'GET_LOCALE_SUCCESS'
export const GET_LOCALE_FAILED = 'GET_LOCALE_FAILED'

export const getLocale: ActionCreator<ReduxAction> = () => ({
    type: GET_LOCALE_PENDING,
})

export const getLocaleSuccess: ActionCreator<ReduxAction> = payload => ({
    type: GET_LOCALE_SUCCESS,
    payload,
})

export const getLocaleFailed: ActionCreator<ErrorReduxAction> = error => ({
    type: GET_LOCALE_FAILED,
    error,
})

import { Observable } from 'rxjs'
import { Util } from 'expo'

export class LanguageApi {
    formatLocale(locale: string): string {
        return locale.split('_')[1]
    }

    getLanguage() {
        return Observable.fromPromise(Util.getCurrentLocaleAsync()).map(
            this.formatLocale
        )
    }
}

export default new LanguageApi()

import { Epic, ActionsObservable } from 'redux-observable'

import { Observable } from 'rxjs'

import config from 'config'
import { AppState, ReduxAction, EpicDependancies } from '../types'
import * as languageActions from './actions'

const getLocaleEpic: Epic<ReduxAction, AppState, EpicDependancies> = (
    action$,
    store,
    { languageApi }
) => {
    return action$.ofType(languageActions.GET_LOCALE_PENDING).mergeMap(() => {
        return languageApi
            .getLanguage()
            .map((locale: string) => {
                const formattedLocale = config.LANGUAGE.SUPPORTED_LOCALES.includes(
                    locale
                )
                    ? locale
                    : config.LANGUAGE.DEFAULT_LOCALE

                return languageActions.getLocaleSuccess(formattedLocale)
            })
            .catch((err: Error) =>
                Observable.of(languageActions.getLocaleFailed(err))
            )
    })
}

export default getLocaleEpic

import { Reducer } from 'redux'

import config from 'config'
import { ReduxAction } from '../types'
import * as LanguageActions from './actions'
import { LanguageState } from './types'

export const initialState: LanguageState = {
    locale: config.LANGUAGE.DEFAULT_LOCALE,
    pending: false,
}

const languageReducer: Reducer<LanguageState> = (
    state = initialState,
    action: ReduxAction
) => {
    switch (action.type) {
        case LanguageActions.GET_LOCALE_PENDING:
            return {
                ...state,
                pending: true,
            }
        case LanguageActions.GET_LOCALE_SUCCESS:
            return {
                ...state,
                pending: false,
                locale: action.payload,
            }
        default:
            return state
    }
}

export default languageReducer

import { createSelector, OutputSelector, Selector } from 'reselect'
